css:
.table{
    border-collapse: collapse;
    margin: 3px 0;
}

.print-format label {
    text-align: left;
}

.print-format .important .value {
    text-align: right;
    font-size:100%!important;
    display: contents;
    font-weight: 600;
}

.print-format .value {
    text-align: left;

}

.table-sr {
    display: none !important;
}


.print-format .table-bordered, .print-format .table-bordered > thead > tr > th, .print-format .table-bordered > tbody > tr > th, .print-format .table-bordered > tfoot > tr > th, .print-format .table-bordered > thead > tr > td, .print-format .table-bordered > tbody > tr > td, .print-format .table-bordered > tfoot > tr > td {
    border: none !important;
    text-align: left;
    padding:None!important;
}

.table > tbody > tr > td {
    line-height: 0.9 !important;
}

@media all {
  .print-format {
       max-width: 100%;
       font-family: Monospaced, sans-serif !important;
       font-size: 8.0pt!important;
}
     
  
}

.print-format * {
    color: #0D131A !important;
    font-weight: 600;
}

.print-format print-format-preview {
    display: content!important;

}


[data-fieldtype="Date"] .value { 
    text-align: left; 
    
}

[data-fieldtype="Currency"] .value { 
    text-align: right;
    font-weight: bold;

}

[data-fieldtype="Table"] { 
    display: content;
}

[data-fieldname="rounded_total"] { 
    text-align: right;
}

#out_right{
    text-align:right!important;
    padding:0!important;

}
#out_left{
    text-align:left!important;
    padding:0!important;
}
